INSERT INTO weapon_and_ammunition (name_of_type, `description`)
VALUES
('Пистолеты', 'Пистоле́т (фр. pistolet ← фр. pistole от чеш. píšťala «пищаль, дудка») — ручное короткоствольное стрелковое оружие, предназначенное для поражения целей (живой силы и других) на дальности до 25–50 метров. Бывает как огнестрельным, так и пневматическим. Ранние пистолеты были, как правило, однозарядными, с гладким или нарезным стволом. Современные пистолеты в большинстве своем самозарядные, нарезные, со значительным магазином (от 5–7 до 15–20 и более патронов).'),
('Револьверы', 'Револьве́р (от англ. revolve «вращать») — многозарядное короткоствольное стрелковое оружие с вращающимся барабаном, выполняющим функцию магазина. Барабан имеет несколько камор, в которых располагаются боеприпасы. В момент выстрела очередная камора служит патронником.'),
('Винтовки', 'Винтовка (сокр. от «пищаль-винтовка», от «винтовальная пищаль», от винт, в свою очередь, предположительно от нем. Gewinde «нарезка; резьба») — нарезное стрелковое оружие, конструктивно предназначенное для удержания и управления при стрельбе двумя руками с упором приклада в плечо.'),
('Снайперские винтовки', 'Снайперская винтовка — боевая винтовка, конструкция которой обеспечивает повышенную точность стрельбы'),
('Автоматы', 'Автома́т, в иностранной литературе также штурмова́я винто́вка (англ. assault rifle) — ручное индивидуальное автоматическое огнестрельное стрелковое оружие, предназначенное для поражения живой силы противника в ближнем бою и способное создавать высокую плотность огня.'),
('Карабины', 'Караби́н — облегчённая винтовка с укороченным стволом.'),
('Ружья', 'Ружьё — в современном узком значении — длинноствольное стрелковое оружие, предназначенное для удержания и управления при стрельбе двумя руками с упором приклада в плечо, гладкоствольное или комбинированное (с гладкими и нарезными стволами), стреляющее пулей или дробью.'),
('Ручные пулемёты', 'Ручной пулемёт — лёгкий пулемёт, стрелковое автоматическое оружие поддержки, допускающее переноску одним военнослужащим, дающее возможность вести стрельбу без использования станка и предназначенное для поражения пулями различных наземных, надводных и воздушных целей.'),
('Пистолеты-пулемёты', 'Пистолет-пулемёт (ПП) — индивидуальное ручное автоматическое стрелковое оружие непрерывного огня, использующее для стрельбы пистолетный патрон.'),
('Боеприпасы', 'Боеприпасы для разных видов оружия');
