INSERT INTO arms_manufacturers (`name`, year_of_foundation, country, `description`)
VALUES
('Beretta', 1526, 'Италия', 'Беретта (итал. Beretta, полное название итал. Fabbrica d\'Armi Pietro Beretta Gardone) — одна из главных итальянских компаний по производству оружия и старейшая оружейная компания в мире. Её продукция стоит на вооружении полиции и армии во многих странах мира, широко используется для самообороны гражданскими лицами.'),
('Glock GmbH', 1963, 'Австрия', 'Glock GmbH (по-русски произносится Глок ГмбХ) — австрийская фирма — производитель оружия, основанная в 1963 году в Дойч-Ваграме (Deutsch-Wagram), городке неподалёку от Вены, Австрия. Названа по фамилии её основателя — Гастона Глока. Наибольшую популярность приобрела за счёт своих пистолетов, однако также производит ножи и пехотные лопатки.'),
('Walther', 1886, 'Германия', 'Carl Walther GmbH Sportwaffen — немецкая компания, основана в 1886 году Карлом Вальтером в городе Целла-Мелис, Тюрингия. Штаб-квартира компании находится в Ульме и Арнсберге. В 1993 году фирма Walther вошла в немецкий холдинг Umarex'),
('Heckler & Koch', 1948, 'Германия', 'Heckler & Koch GmbH — немецкая компания по производству стрелкового оружия, основанная в 1949 году. Один из ведущих поставщиков армии и полиции Германии и других стран мира. Штаб-квартира компании расположена в городе Оберндорф-на-Неккаре, земля Баден-Вюртемберг.'),
('Smith & Wesson', 1852, 'США', 'Smith & Wesson Firearms Co. — американская компания, производитель огнестрельного оружия. Известна производством револьверов. Компания расположена в Спрингфилде, штат Массачусетс. Основана в 1852 году Хорасом Смитом и Дэниелом Б. Вессоном.'),
('Colt’s Manufacturing Company', 1836, 'США', 'Colt’s Manufacturing Company (CMC) (Кольт Меньюфекчеринг Компани) — компания-производитель огнестрельного оружия, в том числе очень популярных американских пистолетов и револьверов под объединённым названием Кольт. Компания расположена в Хартфорде, штат Коннектикут США. Производит оружие для армейских и полицейских структур, спортивные и охотничьи ружья и винтовки.'),
('Springfield Armory', 1777, 'США', 'Спрингфилдский арсенал (англ. Springfield Armory) — один из основных американских центров по разработке и производству огнестрельного оружия, находящийся в городе Спрингфилд, штат Массачусетс. Был первым федеральным арсеналом США, функционировал с 1777 по 1968 год. В настоящее время используется как музейный комплекс Springfield Armory National Historic Site.'),
('Mauser', 1872, 'Германия', 'Гебрюдер Маузер (нем. Gebrüder Mauser) — немецкая компания, основанная братьями Петером-Паулем и Вильгельмом Маузерами для производства стрелкового оружия (преимущественно винтовок).'),
('АО «ЦНИИточмаш»', 1944, 'Россия', 'АО «ЦНИИточмаш» — Центральный научно-исследовательский институт точного машиностроения, находится в городе Подольск (микрорайон Климовск) Московской области. Входит в состав госкорпорации «Ростех». Приказ об организации подписан наркомом вооружения 17 мая 1944 года. Первоначальное название: Научно-исследовательский институт стрелково-пушечного вооружения авиации (НИИСПВА).'),
('Barrett Firearms', 1982, 'США', 'Barrett Firearms (по-русски произносится Барретт, полное название англ. Barrett Firearms Manufacturing) — американская компания по производству стрелкового оружия, оптических приборов и амуниции, основанная в 1982 году. Штаб-квартира находится в городе Мерфрисборо, штат Теннесси. Компания специализируется в основном на производстве крупнокалиберных снайперских винтовок.'),
('FN Herstal', 1889, 'Бельгия', 'FN Herstal (фр. Fabrique Nationale d’Herstal, варианты названия:Fabrique Nationale, FN, «Национальная фабрика в Эрстале») — бельгийская оружейная компания, входящая в состав Herstal Group, которая также владеет такими производителями оружия, как американские Winchester Repeating Arms Company и Browning Arms Company.'),
('Группа компаний «Кала́шников»', 1807, 'Россия', 'Группа компаний «Кала́шников» — многопрофильный холдинг по производству продукции гражданского и военного назначения, предприятие в составе стрелкового сектора ОПК России, производитель боевого автоматического и снайперского оружия, управляемых артиллерийских снарядов, а также высокоточного оружия. Сегмент гражданской продукции включает охотничьи ружья, спортивные винтовки, станки и инструмент.'),
('O.F. Mossberg & Sons', 1919, 'США', 'O.F. Mossberg & Sons (известная также как Mossberg) — американская компания, производящая огнестрельное оружие (в частности, охотничьи ружья, винтовки, телескопические прицелы и аксессуары для оружия. В период с 1940-х по 60-е гг, она также выпускала спортивные винтовки под патрон кольцевого воспламенения 22-го калибра.'),
('Česká zbrojovka Uherský Brod', 1936, 'Чехия', 'Česká zbrojovka a. s. Uherský Brod (АО «Ческа Збройовка Угерски-Брод») (ČZUB) — машиностроительная компания, базирующаяся в городе Угерски-Брод, основной деятельностью которой является производство служебного, охотничьего и спортивного огнестрельного оружия. Владельцем является чешская компания «ČZ Partners SE», которой также принадлежат другие компании с родственными производственными программами.'),
('Israel Military Industries', 1933, 'Израиль', 'Israel Military Industries (ивр. ‏התעשייה הצבאית‏‎ — Военная промышленность) — израильская военная государственная корпорация, производящая системы вооружения, боеприпасы, а также ракетную и бронетехнику[1]. Основной поставщик вооружений для Армии обороны Израиля.');
INSERT INTO ammunition (name_of_ammo, type_ammo, cartridge_length, bullet_mass, bullet_muzzle_velocity, `description`, photo_id)
VALUES
('9×19 мм Парабеллум', 'Центрального воспламенения', 29.69, 7.45, 360, '9×19 мм Парабеллум — пистолетный унитарный патрон с бесфланцевой гильзой цилиндрической формы с небольшой конусностью, разработанный в 1902 году австрийским оружейником Георгом Люгером под созданный им в 1898 году самозарядный пистолет Пистолет Люгера (Люгер, Парабеллум; P08, Parabellum, Borchardt-Luger) калибра 9 мм, появившийся в 1903 году.', 2),
('.500 S&W Magnum', 'Центрального воспламенения', 57.2, 19, 632, '.500 S&W Magnum — пистолетный патрон крупного калибра созданный под торговой маркой Cor-Bon в сотрудничестве с инженерной группой компании Smith & Wesson для использования в револьвере Smith & Wesson Model 500. Разрабатывался как охотничий патрон с целью эффективного поражения любых животных североамериканской фауны.', 8),
('.44 Magnum', 'Центрального воспламенения', 41, 13, 452, '.44 Remington Magnum или просто .44 Magnum — револьверный боеприпас высокой мощности, разработанный на базе удлиненного патрона .44 S&W Special; помимо револьверов используется в некоторых карабинах и винтовках.', 9),
('.30-06 Springfield', 'Центрального воспламенения', 84.50, 6.5, 815, '7,62×63 мм (.30-06 Springfield) — стандартный винтовочный и пулемётный унитарный патрон США первой половины XX века (с 1906 до 1952 гг.), который использовался в ходе Первой и Второй мировых войн и войны в Корее. Применяется до настоящего времени как популярный охотничий патрон, а также для спортивной стрельбы.', 13),
('7,92 × 57 мм', 'Центрального воспламенения', 82, 12.8, 755, '7,92×57 мм (7,92×57 Mauser, 8×57 Mauser, 8mm Mauser, 7,9 mm S-Patrone) — немецкий винтовочно-пулемётный унитарный патрон с гильзой бутылочной формы с невыступающей закраиной.', 15),
('12,7 × 99 мм НАТО', 'Центрального воспламенения', 138, 45, 908, '12,7×99 мм (12,7×99 mm NATO, .50 BMG) — крупнокалиберный патрон, разработанный в США. Он был создан в 1920-х годах в качестве боеприпаса для крупнокалиберного пулемёта Browning M1921. После Второй мировой войны патрон стандартизован в НАТО и выпускается различными странами-участниками НАТО по единым техническим условиям.', 16),
('9 × 39 мм', 'Центрального воспламенения', 56.02, 16.1, 290, 'Патрон 9×39 мм — ряд специальных патронов, включающий модификации СП-5, СП-6, ПАБ-9. Первоначально ряд включал в себя только основную модификацию СП-5 и бронебойную СП-6, позже был разработан более дешёвый вариант ПАБ-9. Патроны этих модификаций используются в ВСС «Винторез», АС «Вал», АК-9, ВСК-94, СР-3 «Вихрь», ОЦ-14 «Гроза», АМБ-17 и др.', 18),
('5,56 × 45 мм НАТО', 'Центрального воспламенения', 57.4, 4, 860, '5,56×45 мм (.223 Remington) NATO — малоимпульсный промежуточный патрон с бесфланцевой гильзой бутылочной формы, принятый на вооружение странами НАТО в 1980-х годах.', 21),
('.410 калибр', 'Центрального воспламенения', 65, 8, NULL, '.410 калибр (.410 bore) - (диаметр пули 10.2-10.4мм). Считается самым маломощным ружейным патроном. Используется для спортивно-тренировочной и развлекательной стрельбы, самообороны и охоты на мелких животных.', 24),
('12 калибр', 'Центрального воспламенения', 76, NULL, NULL, '12 калибр (12 gauge) — самый распространённый в настоящее время калибр для ружейных патронов. Используется для спортивно-тренировочной и развлекательной стрельбы, самообороны и охоты.', 26),
('7,62 × 51 мм НАТО', 'Центрального воспламенения', 69.85, 10.2, 840, '7,62×51 мм NATO (официальное обозначение 7.62 NATO) — стандартный винтовочно-пулемётный боеприпас стран — участниц НАТО. Принят в 1954 году под обозначением T65, впоследствии неоднократно модернизирован.', 28),
('4,6 × 30 мм', 'Центрального воспламенения', 38.5, 1.6, 725, '4,6×30 мм — пистолетный патрон, созданный в 2001 году в рамках программы «Персональное оружие самообороны». Этот патрон, как и 5,7×28 мм, занял нишу между пистолетными и автоматными патронами. Патрон 4,6×30 мм используется в пистолете-пулемёте HK MP7.', 32);
INSERT INTO guns (`type`, `name`, manufacturer, years_of_production, dimensions, weight, types_of_ammo, caliber, work_principles, sighting_range, type_of_ammunition, `description`, photo_id)
VALUES
(1, 'Beretta Px4 Storm', 1, '2004 — настоящее время','{"Длина": 192, "Ширина": 36, "Высота": 140}', 785, 1, 9, 'Отдача при коротком ходе ствола, запирание поворотом ствола', 50, 'Магазин на 10, 14, 17 или расширенный на 20 патронов', 'Беретта Px4 Storm — самозарядный пистолет производства итальянской фирмы Beretta, предназначенный для самообороны и правоохранительных органов.', 1),
(1, 'Beretta 92', 1, 'с 1975', '{"Длина": 217, "Ширина": 38, "Высота": 137}', 950, 1, 9, 'Отдача при коротком ходе ствола, рычажное запирание. ударно-спусковой механизм двойного действия', 50, 'Двухрядный магазин на 15 патронов','Беретта 92 (итал. Beretta 92, также Beretta 96 и Beretta 98) — семейство самозарядных пистолетов, разработанных в 1972 — 1975 годах Карлом Беретта, Джузеппе Мазетти и Витторио Валле — опытнейшими мастерами огнестрельного оружия из итальянской компании Beretta.', 3),
(1, 'Glock 17', 2, '1982 год — настоящее время', '{"Длина": 204, "Ширина": 33, "Высота": 138}', 905, 1, 9, 'Отдача ствола при его коротком ходе', 50, 'Магазин на 17 (стандартный), 19 или 33 патрона','Glock 17 (Глок 17) — австрийский пистолет, разработанный фирмой Glock для нужд Вооружённых сил Австрии.', 4),
(1, 'Walther P99', 3, 'c 1999', '{"Длина": 180, "Ширина": 29, "Высота": 135}', 700, 1, 9, 'Отдача при коротком ходе ствола', NULL, 'Магазин на 16 патронов', 'Вальтер П99, Walther P99 — немецкий пистолет, разрабатывался оружейной компанией «Carl Walther Sportwaffen GmbH» (город Ульм-на-Дунае), с начала 1990-х годов.', 5),
(1, 'HK USP', 4, '1993 — по сей день', '{"Длина": 194, "Ширина": 29, "Высота": 146}', 748, 1, 9, 'Отдача ствола при его коротком ходе', 50, 'Магазин на 15 патронов', 'Heckler & Koch USP (нем. Universale Selbstlade Pistole — универсальный самозарядный пистолет) — самозарядный пистолет, разработанный немецкой компанией Heckler & Koch. Впервые представлен в 1993 году. Предназначен для вооружения полиции и армии.', 6),
(2, 'Smith & Wesson Model 500', 5, '2003–настоящее время', NULL, 1590, 2, 50, 'Ударный', 50, '5-зарядный барабан', 'Smith & Wesson Model 500 — пятизарядный револьвер барабанного типа, разработанный оружейной компанией Smith & Wesson в 2003 году под патрон S&W Magnum калибра .500 и .50. Предназначен для охоты на крупную дичь, например на медведя гризли.', 7),
(2, 'Colt Anaconda', 6, '1990–1999', NULL, 1500, 3, 44, 'Двойного действия', NULL, '6-зарядный барабан', 'Colt Anaconda — крупнокалиберный револьвер с ударно-спусковым механизмом двойного действия, выпускавшийся в 1990—1999 годах на Colt’s Manufacturing Company. Anaconda похож на Colt Cobra, но имеет усиленную раму и барабан для использования более мощных патронов типа .44 Magnum и .45 Colt. Из-за большого веса применение Colt Anaconda в качестве служебного оружия признано нецелесообразным; револьвер, в основном, используется для охоты и спорта.', 10),
(2, 'Smith & Wesson Model 29', 5, '1955 год — настоящее время', NULL, 1220, 3, 44, 'Двойного действия', NULL, '6-зарядный барабан', 'Smith & Wesson Model 29 — американский револьвер двойного действия под патрон .44 Magnum. Один из наиболее массовых револьверов Smith & Wesson. На момент запуска в производство в 1955 году и долгое время после позиционировался как самое мощное ручное стрелковое оружие в мире. Часто используется в видеоиграх и фильмах.', 11),
(3, 'Springfield M1903', 7, '1903–наст. время', NULL, 3950, 4, 7.62, 'Скользящий затвор', 550, 'Постоянный магазин на 5 патронов', 'Springfield M1903 (Спрингфилд обр. 1903 г., формально United States Rifle, Caliber .30, Model 1903) — американская магазинная винтовка.', 12),
(3, 'Mauser 98k', 8, '1935 — по настоящее время', NULL, 3700, 5, 7.9, 'Скользящий затвор', 1500, 'Неотъёмный магазин на 5 патронов, снаряжаемый обоймами', 'Mauser 98k (Маузер 98k) — магазинная винтовка (в немецких источниках: Karabiner 98k, Kar98k или K98k), официально принятая на вооружение в 1935 году. Являлась основным и наиболее массовым стрелковым оружием вермахта. Конструктивно является укороченной и незначительно изменённой модификацией винтовки Mauser 98.', 14),
(4, 'Barrett M82', 10, '1989—н. в.', NULL, 13500, 6, 12.7, 'Отдача с коротким ходом ствола, поворотный затвор', 1500, 'Магазин на 10 патронов', 'Barrett M82, в документации Вооружённых сил США M107 — американская самозарядная крупнокалиберная снайперская винтовка, выпускаемая компанией Barrett Firearms Manufacturing и состоящая на вооружении ряда стран мира.', 17),
(4, 'ВСС', 9, '1987 год — настоящее время', NULL, 2600, 7, 9, 'Отвод пороховых газов, поворотный затвор', 420, 'Коробчатые магазины на 10 или 20,30 патронов', 'ВСС (Винтовка снайперская специальная) — бесшумная снайперская винтовка для подразделений специального назначения. Индекс ГРАУ — 6П29. Разработана в 1980-х годах в ЦНИИточмаш г. Климовска под руководством П. И. Сердюкова и при участии В. Ф. Красникова. Название «Винторез» осталось в обиходе после его использования в конструкторской документации.', 19),
(5, 'АС «Вал»', 9, '1987 год — настоящее время', NULL, 2500, 7, 9, 'Отвод пороховых газов, поворотный затвор', 420, 'Коробчатый магазин на 20 патронов (возможно использование магазинов от ВСС на 10 патронов и СР-3М на 30 патронов)', 'АС «Вал» (Автомат Специальный, Индекс ГРАУ — 6П30) — бесшумный автомат, разработанный в климовском ЦНИИточмаш конструкторами П. Сердюковым и В. Красниковым во второй половине 1980-х годов на базе бесшумной снайперской винтовки «ВСС». «Вал» и «ВСС» визуально похожи и унифицированы на 70 %.', 20),
(5, 'M16', 6, '1964 год — настоящее время', NULL, 2880, 8, 5.56, 'Отвод пороховых газов, поворотный затвор', 450, 'Коробчатый магазин на 20/30 патронов', '5,56-мм автомат M16 (официальное обозначение — Rifle, Caliber 5.56 mm, M16) — американская автоматическая винтовка калибра 5,56 мм, разработанная и принятая на вооружение в 1960-х годах.', 22),
(5, 'FN SCAR', 11, 'с 2003 года', NULL, 3190, 8, 5.56, 'Отвод пороховых газов, поворотный затвор', 400, 'Отъёмный коробчатый магазин на: 30 (SCAR-L) 20 (SCAR-H) патронов', 'FN SCAR (англ. Special Operations Forces Combat Assault Rifle[12] — боевая штурмовая винтовка для сил специальных операций) — оружейная система, разработанная американским подразделением бельгийской компании FN Herstal для участия в конкурсе на создание нового автомата для бойцов USSOCOM в 2004 году.', 23),
(6, 'Сайга', 12, 'c 1970-го года', NULL, 3400, 9, 410, 'Отвод пороховых газов, поворотный затвор', NULL, 'Магазин на 10 патронов', '«Сайга» — охотничье ружье на базе автомата Калашникова, выпускается — Концерном «Калашников». Инженерами завода создано множество различных конструкций гладкоствольного охотничьего оружия 12-го, 20-го, 366-го и 410-го калибров, а также нарезного под патроны 5,45×39 мм, 5,56×45 мм, 7,62×39 мм, .308Win, 9х19 «Парабеллум», также были выпущены небольшие эксклюзивные мини партии под редкие советские патроны 5,6×39 мм и 9×53R мм, причём первым в серии был «Сайга-5,6» под патрон 5,6×39, выпущенный в 1974 году.', 25),
(7, 'Mossberg 500', 13, '1961 год — настоящее время', NULL, 3300, 10, 12, 'Помповый', 40, 'Трубчатый подствольный магазин на 5-9 патронов', '«Моссберг-500» — одно из самых распространённых многозарядных ружей — помповых, с перезарядкой подвижным цевьём. Впервые выпущено в 1961 году. Сейчас выпускается в различных модификациях, предназначенных как для охоты, так и для полиции, охранников и самообороны.', 27),
(8, 'Mark 48', 11, '2003 год — настоящее время', NULL, 8200, 11, 7.62, 'Отвод пороховых газов, поворотный затвор', 800, 'Лента M13', 'Mark 48 — ручной пулемёт под патрон 7,62×51 мм НАТО, выпускаемый бельгийской оружейной компанией FN Herstal.', 29),
(8, 'FN Minimi', 11, '1975 — настоящее время', NULL, 6850, 8, 5.56, 'Отвод пороховых газов, поворотный затвор', 600, '100- или 200-зарядная разъёмно-звеньевая лента M27, 30-зарядный магазин типа STANAG', '5,56-мм ручной пулемёт FN Minimi — ручной пулемёт калибра 5,56×45 мм производства FN Herstal (Бельгия), серийное производство началось в 1980-х. Разработку этого оружия отдали перспективному конструктору Эрнесту Вервьеру (Ernest Vervier), создателю пулемёта FN MAG.', 30),
(8, 'HK MG4', 4, '2005', NULL, 8150, 8, 5.56, 'Отвод пороховых газов, поворотный затвор', 1000, 'Лента на 100 или 200 патронов', 'MG4 (нем. Heckler & Koch MG4 (Maschinengewehr), другое название — HK123) — ручной пулемёт, производимый компанией Heckler & Koch GmbH. Разрабатывался со второй половины 1990-х годов. Первый его прототип впервые был показан широкой публике в 2001 году. Новый пулемёт стал прямым конкурентом такому популярному образцу, как бельгийский FN Minimi / M249 SAW, и предназначен на ту же роль - лёгкого и мобильного оружия огневой поддержки уровня пехотного отделения.', 31),
(9, 'HK MP5', 4, 'с 1966 по н.в.', '{"Длина": 680, "Ширина": 50, "Высота": 260}', 2540, 1, 9, 'Полусвободный затвор, стрельба с закрытого затвора', 200, 'Коробчатый магазин на 30 или 40 патронов', 'Heckler & Koch MP5 (Maschinenpistole 5 — пистолет-пулемёт, модель № 5) — семейство пистолетов-пулемётов, разработанных немецким производителем стрелкового оружия, фирмой Heckler & Koch (HK) в 1960-х годах на основе HK G3.', 33),
(9, 'HK MP7', 4, '2001 — н. в.', '{"Длина": 638, "Ширина": 51, "Высота": 169.5}', 1800, 1, 9, 'Отвод пороховых газов, поворотный затвор', 200, 'Коробчатые магазины на 20/40 патронов', 'Heckler & Koch MP7 A1 PDW — пистолет-пулемёт, разработанный в начале 2000-х годов немецкой фирмой Heckler & Koch.', 34),
(9, 'Uzi', 15, '1954', NULL, 3500, 1, 9, 'Свободный затвор', 200, 'Коробчатый магазин на 25, 32, 40, 50 или 62 патрона', 'У́зи (ивр. ‏עוזי‏‎) — семейство пистолетов-пулемётов (ПП), выпускаемых израильским концерном Israel Military Industries (IMI). Название «Узи» было дано в честь конструктора оружия Узиэля Галя.', 35),
(9, 'IMI Mini Uzi', 15, '1984 — настоящее время', NULL, 2750, 1, 9, 'Свободный затвор (имеются варианты, стреляющие как с открытого, так и с закрытого затвора)', NULL, 'Коробчатый магазин на 20, 25 или 32 патрона', 'IMI Mini Uzi — модификация пистолет-пулемёта Uzi. Масса уменьшена за счет облегчения затвора, сокращения длины ствола, оснащения пистолет-пулемёта короткой возвратно-боевой пружиной.', 36),
(9, 'Cz Scorpion EVO 3 A1', 14, '2010 — настоящее время', NULL, 2770, 1, 9, 'Свободный затвор', 50, 'Прозрачный магазин на 20 или 30 патронов из ударопрочного пластика', 'Česká Zbrojovka Scorpion EVO 3 A1 (также Scorpion EVO III) — пистолет-пулемёт, сконструированный в Словакии под названием Laugo, производимый в Чехии компанией «Česká Zbrojovka Uherský Brod». Министерство обороны Чехии планирует закупить 500 экземпляров данного пистолета-пулемёта в ближайшие несколько лет.', 37);
